    public final double Ki = 0.0;
    public final double Kd = 0.0;

    private Level elevatorState;

    private boolean ballWantsToComeIn;

    // belong to the entry motor
    private CANSparkMax lowerMotor;

    private final PIDController lowerPIDController;

    private RelativeEncoder lowerEncoder;

    // belong to the exit motor
    private CANSparkMax upperMotor;

    private final PIDController upperPIDController;

    private RelativeEncoder upperEncoder;

    /**
    *
    */
    public Elevator() {
        // BEGIN AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=CONSTRUCTORS

    // END AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=CONSTRUCTORS

        lowerPIDController = new PIDController(Kp, Ki, Kd);
        upperPIDController = new PIDController(Kp, Ki, Kd);

        elevatorState = Level.START_LEVEL;
        ballWantsToComeIn = false;

        lowerMotor = new CANSparkMax(8, MotorType.kBrushless);//btn x
        upperMotor = new CANSparkMax(2, MotorType.kBrushless);//btn y

        lowerEncoder = lowerMotor.getEncoder();
        upperEncoder = upperMotor.getEncoder();

        reset();

    }

    @Override
    public void periodic() {
        // This method will be called once per scheduler run

    }

