    public void setEntryMotorPowerManual(double power) {
        lowerMotor.set(power);
    }

    public void setExitMotorPowerManual(double power) {
        upperMotor.set(power);
    }

    public void reset(){
        lowerEncoder.setPosition(0.0);
        upperEncoder.setPosition(0.0);
    }

    public void printCurrentPosition() {
        System.out.println(
                "Entry Current Position: " + lowerEncoder.getPosition() + " Entry Current Speed: " + lowerMotor.get());
        System.out.println(
                "Exit Current Position: " + upperEncoder.getPosition() + " Exit Current Speed: " + upperMotor.get());
    }

    public boolean atEntrySetPoint(){
        return (lowerEncoder.getPosition() >= entryPosition-0.1);
    }

    public boolean atExitSetPoint(){
        return (upperEncoder.getPosition() >= exitPosition-0.1);
    }
